import { useState, useEffect, useCallback } from 'react';
import Card from '../UI/Card';
import classes from './AvailableMeals.module.css';
import MealItem from './MealItem/MealItem';

const DUMMY_MEALS = [
    {
      id: 'm1',
      name: 'Sushi',
      description: 'Finest fish and veggies',
      price: 22.99,
    },
    {
      id: 'm2',
      name: 'Schnitzel',
      description: 'A german specialty!',
      price: 16.5,
    },
    {
      id: 'm3',
      name: 'Barbecue Burger',
      description: 'American, raw, meaty',
      price: 12.99,
    },
    {
      id: 'm4',
      name: 'Green Bowl',
      description: 'Healthy...and green...',
      price: 18.99,
    },
  ];

const AvailableMeals = () => {

  const [meals,setMeals] = useState([]);
  const [isLoading,setIsLoading] = useState(false);
  const [isError,setIsError] = useState(null);

  const fetchMealHandler = useCallback(async () => {
    setIsLoading(true);
    setIsError(null);
    try {
      const response = await fetch("https://localhost:7118/api/Meals");

      if(!response.ok){
        throw new Error("Something went wrong");
      }

      const data = await response.json();

      const transformedMealData = data.map((item) => {
      return {
        id : item.mealID,
        name : item.name,
        description : item.description,
        price : item.price
      }});

      setMeals(transformedMealData);

    } catch (error) {
      setIsError(error);
    }
    setIsLoading(false);
  },[]);

  useEffect(() => {
    fetchMealHandler();
  }, [fetchMealHandler])
  const mealsList = meals.map((meal) => <MealItem key={meal.id} meal={meal} id={meal.id}/>)

  

  return (
  <section className={classes.meals}>
      <Card>
          <ul>
              {mealsList}
          </ul>
      </Card>
  </section>)

}

export default AvailableMeals;
